import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPortfolioSchema, insertCaseStudySchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {

  // Get all portfolios with optional filtering
  app.get("/api/portfolios", async (req, res) => {
    try {
      const { category, featured } = req.query;
      const portfolios = await storage.getPortfolios(
        category as string,
        featured === "true" ? true : featured === "false" ? false : undefined
      );
      res.json(portfolios);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch portfolios" });
    }
  });

  // Get portfolio by ID
  app.get("/api/portfolios/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const portfolio = await storage.getPortfolioById(id);

      if (!portfolio) {
        return res.status(404).json({ error: "Portfolio not found" });
      }

      res.json(portfolio);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch portfolio" });
    }
  });

  // Create new portfolio
  app.post("/api/portfolios", async (req, res) => {
    try {
      const validatedData = insertPortfolioSchema.parse(req.body);
      const portfolio = await storage.createPortfolio(validatedData);
      res.status(201).json(portfolio);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid portfolio data", details: error.errors });
      }
      res.status(500).json({ error: "Failed to create portfolio" });
    }
  });

  // Get case study by portfolio ID
  app.get("/api/portfolios/:portfolioId/case-study", async (req, res) => {
    try {
      const { portfolioId } = req.params;
      const caseStudy = await storage.getCaseStudyByPortfolioId(portfolioId);

      if (!caseStudy) {
        return res.status(404).json({ error: "Case study not found" });
      }

      res.json(caseStudy);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch case study" });
    }
  });

  // Create new case study
  app.post("/api/case-studies", async (req, res) => {
    try {
      const validatedData = insertCaseStudySchema.parse(req.body);
      const caseStudy = await storage.createCaseStudy(validatedData);
      res.status(201).json(caseStudy);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid case study data", details: error.errors });
      }
      res.status(500).json({ error: "Failed to create case study" });
    }
  });

  // Search portfolios
  app.get("/api/search", async (req, res) => {
    try {
      const { q, category } = req.query;
      let portfolios = await storage.getPortfolios(category as string);

      if (q) {
        const searchTerm = (q as string).toLowerCase();
        portfolios = portfolios.filter(portfolio =>
          portfolio.title.toLowerCase().includes(searchTerm) ||
          portfolio.description.toLowerCase().includes(searchTerm) ||
          portfolio.businessName.toLowerCase().includes(searchTerm) ||
          portfolio.location.toLowerCase().includes(searchTerm)
        );
      }

      res.json(portfolios);
    } catch (error) {
      res.status(500).json({ error: "Search failed" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

