import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/header";
import Footer from "@/components/footer";
import FilterBar from "@/components/filter-bar";
import PortfolioCard from "@/components/portfolio-card";
import CaseStudyModal from "@/components/case-study-modal";
import ImageLightbox from "@/components/image-lightbox";
import { Button } from "@/components/ui/button";
import type { Portfolio } from "@shared/schema";

export default function Home() {
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [sortBy, setSortBy] = useState<string>("recent");
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [selectedPortfolio, setSelectedPortfolio] = useState<Portfolio | null>(null);
  const [lightboxImage, setLightboxImage] = useState<string | null>(null);

  const { data: portfolios = [], isLoading } = useQuery<Portfolio[]>({
    queryKey: ["/api/portfolios", selectedCategory === "all" ? undefined : selectedCategory],
    enabled: true,
  });

  const filteredPortfolios = portfolios.filter(portfolio => {
    if (searchQuery) {
      return (
        portfolio.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        portfolio.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
        portfolio.businessName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        portfolio.location.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }
    return true;
  });

  const sortedPortfolios = [...filteredPortfolios].sort((a, b) => {
    switch (sortBy) {
      case "popular":
        return b.reviewCount - a.reviewCount;
      case "rating":
        return parseFloat(b.rating) - parseFloat(a.rating);
      case "az":
        return a.title.localeCompare(b.title);
      case "recent":
      default:
        return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
    }
  });

  return (
    <div className="min-h-screen bg-pet-cream">
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-pet-beige to-pet-cream py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="font-poppins font-bold text-4xl md:text-6xl text-pet-charcoal mb-6">
            Discover Amazing <br />
            <span className="text-pet-orange">Pet Care</span> Portfolios
          </h1>
          <p className="text-lg text-pet-gray mb-8 max-w-2xl mx-auto">
            Explore detailed case studies from top pet care professionals. Find inspiration for grooming, training, veterinary care, and more.
          </p>
          <div className="flex flex-col sm:flex-row justify-center items-center space-y-4 sm:space-y-0 sm:space-x-4">
            <Button 
              className="bg-pet-orange text-white px-8 py-3 rounded-lg font-medium hover:bg-opacity-90 transition-all duration-200 transform hover:scale-105"
              data-testid="button-browse-portfolios"
            >
              Browse Portfolios
            </Button>
            <Button 
              variant="outline"
              className="border-pet-orange text-pet-orange px-8 py-3 rounded-lg font-medium hover:bg-pet-orange hover:text-white transition-all duration-200"
              data-testid="button-view-featured"
            >
              View Featured Cases
            </Button>
          </div>
        </div>
      </section>

      <FilterBar
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
        viewMode={viewMode}
        onViewModeChange={setViewMode}
        sortBy={sortBy}
        onSortChange={setSortBy}
      />

      {/* Portfolio Grid */}
      <section className="py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="bg-white rounded-xl shadow-lg overflow-hidden animate-pulse">
                  <div className="w-full h-48 bg-gray-200" />
                  <div className="p-6 space-y-4">
                    <div className="h-6 bg-gray-200 rounded w-3/4" />
                    <div className="h-4 bg-gray-200 rounded w-full" />
                    <div className="h-4 bg-gray-200 rounded w-2/3" />
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div 
              className={`grid gap-8 ${
                viewMode === "grid" 
                  ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3" 
                  : "grid-cols-1"
              }`}
              data-testid="portfolio-grid"
            >
              {sortedPortfolios.map((portfolio) => (
                <PortfolioCard
                  key={portfolio.id}
                  portfolio={portfolio}
                  viewMode={viewMode}
                  onViewCaseStudy={() => setSelectedPortfolio(portfolio)}
                  onImageClick={() => setLightboxImage(portfolio.afterImage)}
                />
              ))}
            </div>
          )}

          {sortedPortfolios.length === 0 && !isLoading && (
            <div className="text-center py-16">
              <div className="text-6xl mb-4">🐾</div>
              <h3 className="text-xl font-semibold text-pet-charcoal mb-2">
                No portfolios found
              </h3>
              <p className="text-pet-gray">
                {searchQuery 
                  ? "Try adjusting your search terms or filters"
                  : "Check back later for more amazing pet care stories"
                }
              </p>
            </div>
          )}

          {sortedPortfolios.length > 0 && (
            <div className="text-center mt-12">
              <Button 
                variant="outline"
                className="border-pet-orange text-pet-orange px-8 py-3 rounded-lg font-medium hover:bg-pet-orange hover:text-white transition-all duration-200"
                data-testid="button-load-more"
              >
                Load More Portfolios
              </Button>
            </div>
          )}
        </div>
      </section>

      <Footer />

      {selectedPortfolio && (
        <CaseStudyModal
          portfolio={selectedPortfolio}
          isOpen={!!selectedPortfolio}
          onClose={() => setSelectedPortfolio(null)}
          onImageClick={setLightboxImage}
        />
      )}

      {lightboxImage && (
        <ImageLightbox
          image={lightboxImage}
          isOpen={!!lightboxImage}
          onClose={() => setLightboxImage(null)}
        />
      )}
    </div>
  );
}

